import React, {useState} from "react";
import {ethers} from "ethers";
import {contractAbi, contractAddress} from "../Constant/constant";

function CreateVotingSession() {
  const [sessionName, setSessionName] = useState("");
  const [duration, setDuration] = useState("");

  async function createSession() {
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    await provider.send("eth_requestAccounts", []);
    const signer = provider.getSigner();
    const contractInstance = new ethers.Contract(
      contractAddress,
      contractAbi,
      signer
    );

    const tx = await contractInstance.createVotingSession(
      sessionName,
      duration
    );
    await tx.wait();
    alert("New voting session created successfully!");
  }

  return (
    <div>
      <input
        type="text"
        value={sessionName}
        onChange={(e) => setSessionName(e.target.value)}
        placeholder="Voting Session Name"
      />
      <input
        type="text"
        value={duration}
        onChange={(e) => setDuration(e.target.value)}
        placeholder="Duration (in seconds)"
      />
      <button onClick={createSession}>Create Voting Session</button>
    </div>
  );
}

export default CreateVotingSession;
